import React, { useEffect, useState } from 'react'
import './Details.css'
import { useParams } from 'react-router-dom'
import ProductCard from '../../components/ProductCard';
function Details() {
  const {id}= useParams();
  const {tarif,setTarif}= useState(null)
const url="http://localhost:3001/tarifler/"+id

  useEffect(()=>{

    fetch(url).then(res=>res.json()).then(data=>setTarif(data))
  }, [url])
  return (
    <div className='row mt-3'>
{tarif &&
( <>

  <div className="col-4">
  <img src={`img/${tarif.resim}`} alt={tarif.baslik} />
  <div className="card-body">
            <h5 className="card-title">{tarif.baslik}</h5>
            <p className="aciklama">{tarif.aciklama}</p>
        </div>
  </div>
</>)
}    </div>
  )
}

export default Details
